import { AgentOneShot, PromptTemplate, Instruction, FileCache } from '../index';

/**
 * Instruction class are serialized into a XML-like format into the prompt.*
 * This format is easier to parse and offer better performances for LLM models.
 */
export class ExtractImageInstruction extends Instruction {
  public usage =
    'use this action to extract and describe an image from the HTML. images are in the <img> tags.';

  public parameters = {
    url: 'the entire url of the image (not just relative path)',
    description:
      'use the context of the text around the image tag to describe the image',
  };
}

export class ExtractImagesAgent extends AgentOneShot<ExtractImageInstruction> {
  /**
   * The template is completly customizable.
   * It is the developer responsibility to create quality templates.
   *
   * The library offer pre-defined blocks that can be used to create templates:
   *  - Agent.promptActionsBlock() : agent instructions described in XML format
   *  - Agent.promptEnhancersBlock() : prompt engineering technics to improve the quality of the prompt
   */
  protected template = new PromptTemplate({
    template: `You are a scrapper bot capable of extracting images from the HTML of a page.

For each image you find, you will need to extract the URL and describe the image as best as you can.
You are extracting images from this page: {pageUrl}
    
{bodyHTMLBlock}
    
{actionsBlock}

{promptEnhancersBlock}
`,
    inputVariables: [
      'pageUrl',
      'bodyHTMLBlock',
      'actionsBlock',
      'promptEnhancersBlock',
    ],
  });

  private pageUrl: string;
  private bodyHTML: string;

  constructor({ pageUrl, bodyHTML }: { pageUrl: string; bodyHTML: string }) {
    super({
      // We pass our instruction class to the agent
      instructions: [new ExtractImageInstruction()],
      // initialize the cache engine for local debugging
      cacheEngine: new FileCache(),
    });

    this.pageUrl = pageUrl;
    this.bodyHTML = bodyHTML;
  }

  /**
   * This method is called by the agent to format the prompt
   * before passing it to the LLM.
   */
  protected formatPrompt(): Promise<string> {
    return this.template.format({
      pageUrl: this.pageUrl,
      bodyHTMLBlock: this.contentDelimiter({
        content: this.bodyHTML,
        name: 'HTML PAGE CONTENT',
      }),
      actionsBlock: this.promptActionsBlock(),
      promptEnhancersBlock: this.promptEnhancersBlock(),
    });
  }
}

